// This node converts the structured data from a CSV/XLSX file
// into a single Markdown table for the AI to analyze.

// We will only process the first 100 rows to avoid exceeding the AI's token limit.
const itemsToProcess = items.slice(0, 100);

const firstItem = itemsToProcess[0].json;
const headers = Object.keys(firstItem);
const headerLine = `| ${headers.join(' | ')} |`;
const separatorLine = `| ${headers.map(() => '---').join(' | ')} |`;

const rows = itemsToProcess.map(item => {
  return `| ${headers.map(header => item.json[header]).join(' | ')} |`;
});

const markdownTable = [headerLine, separatorLine, ...rows].join('\n');

return [{
  json: {
    fullText: markdownTable
  }
}];

// In Code node after PDF or TXT Extractor
const textChunks = items.map(item => item.json.text);
const fullText = textChunks.join('\n\n'); // Join with double newline for paragraphs
return [{ json: { fullText: fullText } }];
